mapper.select:
SELECT 

FROM t_item a

WHERE a.id = #{id}

SELECT
FROM t_item a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_item SET 	
		nums = #{nums}
WHERE id = #{id}

UPDATE t_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
