mapper.select:
SELECT * FROM sys_dict WHERE del_flag = #{DEL_FLAG_NORMAL}
AND type = #{type}
AND description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
ORDER BY type, sort, update_date DESC

mapper.update:
UPDATE sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT * FROM sys_dict WHERE del_flag = 0
AND type = #{type}
ORDER BY type, sort, update_date DESC

SELECT * FROM sys_dict WHERE del_flag = 0
AND parent_id = #{parentId}
ORDER BY type, sort, update_date DESC
